import React, { useState } from "react";
import "./styles.css";

const App = () => {
  const [prompt, setPrompt] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSend = async () => {
    if (!prompt.trim()) return;

    // Add user message to chat
    const newMessages = [...messages, { text: prompt, sender: "user" }];
    setMessages(newMessages);

    try {
      const response = await fetch("http://127.0.0.1:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });

      const data = await response.json();
      const story = data.story || "Error generating story.";

      // Add AI response to chat
      setMessages([...newMessages, { text: story, sender: "bot" }]);
    } catch (error) {
      setMessages([...newMessages, { text: "Failed to fetch story.", sender: "bot" }]);
    }

    setPrompt(""); // Clear input
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="input-container">
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter a prompt..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default App;
