import React, { useState } from "react";
import "./styles.css";
import { FaPaperPlane, FaUser, FaRobot } from "react-icons/fa";

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    try {
      const response = await fetch("http://127.0.0.1:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: input }),
      });
      const data = await response.json();
      const aiMessage = { text: data.story || "Error generating story.", sender: "ai" };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (error) {
      setMessages((prev) => [...prev, { text: "Error fetching response.", sender: "ai" }]);
    }
  };

  return (
    <div className="chat-container">
      <h1 className="title">Story Generator</h1>
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.sender === "user" ? <FaUser className="icon user-icon" /> : <FaRobot className="icon ai-icon" />}
            <p>{msg.text}</p>
          </div>
        ))}
      </div>
      <div className="input-box">
        <input
          type="text"
          placeholder="Type a story prompt..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}><FaPaperPlane /></button>
      </div>
    </div>
  );
};

export default App;
