import React, { useState } from 'react';
import { FaUser, FaPaperPlane } from 'react-icons/fa';
import './Chat.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { text: input, user: true }];
    setMessages(newMessages);
    setInput('');

    try {
      const response = await fetch('http://127.0.0.1:5000/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: input }),
      });
      const data = await response.json();
      setMessages([...newMessages, { text: data.story, user: false }]);
    } catch (error) {
      setMessages([...newMessages, { text: 'Error generating story.', user: false }]);
    }
  };

  return (
    <div className="chat-container">
      <h1 className="title">Story Generator</h1>
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.user ? 'user' : 'bot'}`}>
            {msg.user && <FaUser className="user-icon" />}
            <span>{msg.text}</span>
          </div>
        ))}
      </div>
      <div className="input-box">
        <input
          type="text"
          placeholder="Type your story idea..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button onClick={sendMessage}><FaPaperPlane /></button>
      </div>
    </div>
  );
}

export default App;
